import java.sql.*;
import java.util.ArrayList;
import java.util.List;

public class ProdutoDAO {

    private Connection conexao;

    // Construtor para inicializar a conex√£o com o banco
    public ProdutoDAO(Connection conexao) {
        this.conexao = conexao;
    }

    // M√©todo para listar todos os produtos
    public List&lt;Produto&gt; getProdutos() {
        List&lt;Produto&gt; produtos = new ArrayList&lt;&gt;();
        String sql = &quot;SELECT * FROM produtos&quot;;

        try (
		    PreparedStatement stmt = conexao.prepareStatement(sql);
            ResultSet rs = stmt.executeQuery()) {

            while (rs.next()) {
                Produto produto = new Produto();
                produto.setId(rs.getInt(&quot;id&quot;));
                produto.setDescricao(rs.getString(&quot;descricao&quot;));
                produto.setPrecoCusto(rs.getDouble(&quot;preco_custo&quot;));
                produtos.add(produto);
            }
        } catch (SQLException e) {
            System.out.println(&quot;Erro ao listar produtos: &quot; + e.getMessage());
        }

        return produtos;
    }

    // M√©todo para inserir um novo produto no banco de dados
    public void inserirProduto(Produto produto) {
        String sql = &quot;INSERT INTO produtos (descricao, preco_custo) VALUES (?, ?)&quot;;

        try (PreparedStatement stmt = conexao.prepareStatement(sql)) {
            stmt.setString(1, produto.getDescricao());
            stmt.setDouble(2, produto.getPrecoCusto());
            stmt.executeUpdate();
        } catch (SQLException e) {
            System.out.println(&quot;Erro ao inserir produto: &quot; + e.getMessage());
        }
    }

    // M√©todo para atualizar um produto no banco de dados
    public void atualizarProduto(Produto produto) {
        String sql = &quot;UPDATE produtos SET descricao = ?, preco_custo = ? WHERE id = ?&quot;;

        try (PreparedStatement stmt = conexao.prepareStatement(sql)) {
            stmt.setString(1, produto.getDescricao());
            stmt.setDouble(2, produto.getPrecoCusto());
            stmt.setInt(3, produto.getId());
            stmt.executeUpdate();
        } catch (SQLException e) {
            System.out.println(&quot;Erro ao atualizar produto: &quot; + e.getMessage());
        }
    }

    // M√©todo para excluir um produto do banco de dados
    public void excluirProduto(int id) {
        String sql = &quot;DELETE FROM produtos WHERE id = ?&quot;;

        try (PreparedStatement stmt = conexao.prepareStatement(sql)) {
            stmt.setInt(1, id);
            stmt.executeUpdate();
        } catch (SQLException e) {
            System.out.println(&quot;Erro ao excluir produto: &quot; + e.getMessage());
        }
    }
}

